import React, {Component} from 'react'
import './App.css'
import Navbar from './Navbar'
import Main from './Main'
import Web3 from 'web3'
import Tether from '../truffle_abis/Tether.json'
import RWD from '../truffle_abis/RWD.json'
import DecentralBank from '../truffle_abis/DecentralBank.json'

class App extends Component {
    // React code goes here
    async UNSAFE_componentWillMount() {
        await this.loadWeb3()
        await this.loadBlockchainData()
    }

    async loadWeb3() {
        if (window.ethereum) {
            window.web3 = new Web3(window.ethereum)
            await window.ethereum.enable()
        } else if (window.web3) {
            window.web3 = new Web3(window.web3.currentProvider)
        } else {
            window.alert('No ethereum browser detected! You can check out MetaMask')
        }
    }

    async loadBlockchainData() {
        const web3 = window.web3
        const account = await web3.eth.getAccounts()
        this.setState({account:account[0]})

        const networkId = await web3.eth.net.getId()

        // Load Tether contract
        const tetherData = Tether.networks[networkId]
        if(tetherData) {
            const tether = new web3.eth.Contract(Tether.abi, tetherData.address)
            this.setState({tether})
            let tetherBalance = await tether.methods.balanceOf(this.state.account).call()
            this.setState({tetherBalance: tetherBalance.toString()})
            console.log({tetherBalance: tetherBalance})
        } else {
            window.alert('Error: Tether contract not deployed')
        }

        // Load Reward (RWD) contract
        const rwdData = RWD.networks[networkId]
        if (rwdData) {
            const rwd = new web3.eth.Contract(RWD.abi, rwdData.address)
            this.setState({rwd})
            let rwdBalance = await rwd.methods.balanceOf(this.state.account).call()
            this.setState({rwdBalance: rwdBalance.toString()})
            console.log({rwdBalance: rwdBalance})
        } else {
            window.alert('Error: RWD contract not deployed')
        }

        // Load DecentralBank contract
        const decentralBankData = DecentralBank.networks[networkId]
        if (decentralBankData) {
            const decentralBank = new web3.eth.Contract(DecentralBank.abi, decentralBankData.address)
            this.setState({decentralBank})
            let stakingBalance = await decentralBank.methods.stakingBalance(this.state.account).call()
            this.setState({stakingBalance: stakingBalance.toString()})
            console.log({stakingBalance: stakingBalance})
        } else {
            window.alert('Error: DecentralBank contract not deployed')
        }

        this.setState({loading: false})
    }

    // Staking function
    // approve transaction, transfer from, send hash
    stakeTokens = (amount) => {
        this.setState({loading:true})
        this.state.tether.methods.approve(this.state.decentralBank._address, amount).send({from: this.state.account}).on('transactionHash', (hash) => {
            this.state.decentralBank.methods.depositTokens(amount).send({from: this.state.account}).on('transactionHash', (hash) => {
                this.setState({loading:false})
            })
        })
    }

    // Unstaking function
    // 
    unstakeTokens = () => {
        this.setState({loading:true})
        this.state.tether.methods.unstakeTokens().send({from: this.state.account}).on('transactionHash', (hash) => {
            this.setState({loading:false})
        })
    }

    constructor(props) {
        super(props)
        this.state = {
            account: '0x...',
            tether: {},
            rwd: {},
            decentralBank: {},
            tetherBalance: '0',
            rwdBalance: '0',
            stakingBalance: '0',
            loading: true

        }
    }

    render() {

        let content
        {this.state.loading ? content = <p id='loader' className='text-center' style={{margin:'30px'}}>Loading Please...</p> : content=
        <Main 
        tetherBalance = {this.state.tetherBalance}
        rwdBalance = {this.state.rwdBalance}
        stakingBalance = {this.state.stakingBalance}
        stakeTokens = {this.stakeTokens}
        unstakeTokens = {this.unstakeTokens}
        />}
        return (
            <div>
                <Navbar account={this.state.account}/>
                <div className='container-fluid mt-5'>
                    'Hello World!!!'
                    <div className='row'>
                        'Row 2'
                        <main role='main' className='col-lg-12 ml-auto mr-auto' style={{maxWidth:'600px', minHeight:'100vm'}}>
                            'Main'
                            <div>
                                {content}
                            </div>
                        </main>
                    </div>
                    <h1>{console.log(this.state.loading)}</h1>
                </div>   
            </div>
        )
    }
}

export default App;
